<?php  // Accès à la base de données  $serveur     = "localhost";  $utilisateur = "travel_planner";  $motDePasse  = "travel_planner";  $base        = "travel_planner";  mysql_connect($serveur, $utilisateur, $motDePasse);  mysql_select_db($base) or die("Base de données inactive. ");  // $items est la chaîne contenant l'objet JSON des données  $items = '';   // Parcours des lieux  $result = mysql_query("SELECT* FROM places WHERE id_country = 22");  WHILE ($row = mysql_fetch_object($result)) {      // Ecriture des données sous format JSON   $items .= <<<EOD   { "LatLng_lieu" : "$row->gps_lat, $row->gps_lon",     "Titre_lieu"  : "$row->name",     "Url_lieu"    : "xxx"},EOD;    }    mysql_free_result($result);    // On enlève la dernière virgule  if ($items != ''){     $items = substr($items, 0, -1);  }    // Ecriture de la liste des lieux en format JSON    header('Content-type: application/json');    ?>    {           "items": [                  <?php echo $items;?>                  ]    }